import React, { useState, useEffect } from 'react';
import { Table, Badge, Alert, Button, Card } from 'react-bootstrap';
import { Servicio } from '../types';
import { servicioService } from '../services/api';

interface ServicioListProps {
  onError: (error: string | null) => void;
  onLoading: (loading: boolean) => void;
}

const ServicioList: React.FC<ServicioListProps> = ({ onError, onLoading }) => {
  const [servicios, setServicios] = useState<Servicio[]>([]);

  const loadServicios = async () => {
    try {
      onLoading(true);
      onError(null);
      const response = await servicioService.getAll();
      setServicios(response.data);
    } catch (error) {
      onError('Error al cargar los servicios. AsegÃºrate de que el backend estÃ© ejecutÃ¡ndose en http://localhost:7080');
    } finally {
      onLoading(false);
    }
  };

  useEffect(() => {
    loadServicios();
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-AR', {
      style: 'currency',
      currency: 'ARS'
    }).format(amount);
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5>Lista de Servicios</h5>
        <Button variant="primary" onClick={loadServicios}>
          ðŸ”„ Actualizar
        </Button>
      </div>

      {servicios.length === 0 ? (
        <Alert variant="info">
          No hay servicios registrados.
        </Alert>
      ) : (
        <div className="row">
          {servicios.map((servicio) => (
            <div key={servicio.id} className="col-md-6 col-lg-4 mb-3">
              <Card className="h-100">
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <strong>{servicio.nombre}</strong>
                  {servicio.disponibilidad ? (
                    <Badge bg="success">Disponible</Badge>
                  ) : (
                    <Badge bg="danger">No Disponible</Badge>
                  )}
                </Card.Header>
                <Card.Body>
                  <p className="card-text">{servicio.descripcion}</p>
                  <div className="d-flex justify-content-between align-items-center">
                    <span className="h5 text-primary mb-0">
                      {formatCurrency(servicio.precio)}
                    </span>
                  </div>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ServicioList;
