import React, { useState, useEffect } from 'react';
import { Table, Badge, Alert, Button } from 'react-bootstrap';
import { Cliente } from '../types';
import { clienteService } from '../services/api';

interface ClienteListProps {
  onError: (error: string | null) => void;
  onLoading: (loading: boolean) => void;
}

const ClienteList: React.FC<ClienteListProps> = ({ onError, onLoading }) => {
  const [clientes, setClientes] = useState<Cliente[]>([]);

  const loadClientes = async () => {
    try {
      onLoading(true);
      onError(null);
      const response = await clienteService.getAll();
      setClientes(response.data);
    } catch (error) {
      onError('Error al cargar los clientes. AsegÃºrate de que el backend estÃ© ejecutÃ¡ndose en http://localhost:7080');
    } finally {
      onLoading(false);
    }
  };

  useEffect(() => {
    loadClientes();
  }, []);

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5>Lista de Clientes</h5>
        <Button variant="primary" onClick={loadClientes}>
          ðŸ”„ Actualizar
        </Button>
      </div>

      {clientes.length === 0 ? (
        <Alert variant="info">
          No hay clientes registrados.
        </Alert>
      ) : (
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>DirecciÃ³n</th>
              <th>TelÃ©fono</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente) => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>
                  <strong>{cliente.nombre}</strong>
                </td>
                <td>{cliente.direccion}</td>
                <td>{cliente.telefono}</td>
                <td>{cliente.correoElectronico}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClienteList;
