import React, { useState, useEffect } from 'react';
import { Table, Badge, Alert, Button } from 'react-bootstrap';
import { Empleado } from '../types';
import { empleadoService } from '../services/api';

interface EmpleadoListProps {
  onError: (error: string) => void;
  onLoading: (loading: boolean) => void;
}

const EmpleadoList: React.FC<EmpleadoListProps> = ({ onError, onLoading }) => {
  const [empleados, setEmpleados] = useState<Empleado[]>([]);

  const loadEmpleados = async () => {
    try {
      onLoading(true);
      onError(null);
      const response = await empleadoService.getAll();
      setEmpleados(response.data);
    } catch (error) {
      onError('Error al cargar los empleados. AsegÃºrate de que el backend estÃ© ejecutÃ¡ndose en http://localhost:7080');
    } finally {
      onLoading(false);
    }
  };

  useEffect(() => {
    loadEmpleados();
  }, []);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('es-AR', {
      style: 'currency',
      currency: 'ARS'
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('es-AR');
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5>Lista de Empleados</h5>
        <Button variant="primary" onClick={loadEmpleados}>
          ðŸ”„ Actualizar
        </Button>
      </div>

      {empleados.length === 0 ? (
        <Alert variant="info">
          No hay empleados registrados.
        </Alert>
      ) : (
        <Table striped bordered hover responsive>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Cargo</th>
              <th>NÂ° IdentificaciÃ³n</th>
              <th>Salario</th>
              <th>Fecha ContrataciÃ³n</th>
            </tr>
          </thead>
          <tbody>
            {empleados.map((empleado) => (
              <tr key={empleado.id}>
                <td>{empleado.id}</td>
                <td>
                  <strong>{empleado.nombre}</strong>
                </td>
                <td>
                  <Badge bg="info">{empleado.cargo}</Badge>
                </td>
                <td>{empleado.numeroIdentificacion}</td>
                <td>{formatCurrency(empleado.salario)}</td>
                <td>{formatDate(empleado.fechaContratacion)}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default EmpleadoList;
